#!/bin/bash

yum -y update
yum -y install targetcli
yum -y install lvm2
systemctl start target
systemctl enable target
systemctl restart target.service
sfdisk /dev/sdc << EOF
;
EOF
sfdisk /dev/sdd << EOF
;
EOF
sfdisk /dev/sde << EOF
;
EOF
sfdisk /dev/sdf << EOF
;
EOF
sfdisk /dev/sdg << EOF
;
EOF
sfdisk /dev/sdh << EOF
;
EOF
sfdisk /dev/sdi << EOF
;
EOF
sfdisk /dev/sdj << EOF
;
EOF
pvcreate /dev/sdc1 /dev/sdd1 /dev/sde1 /dev/sdf1 /dev/sdg1 /dev/sdh1 /dev/sdi1 /dev/sdj1
vgcreate vg1 /dev/sdc1 /dev/sdd1 /dev/sde1 /dev/sdf1 /dev/sdg1 /dev/sdh1 /dev/sdi1 /dev/sdj1
cat <<EOF | base64 --decode >  ~/ose_pvcreate_lun


EOF
firewall-cmd --permanent --add-port=3260/tcp
firewall-cmd --reload
chmod +x ~/ose_pvcreate_lun
cd ~
until $(curl --output /dev/null --silent --head --fail https://master1:8443/api); do
  printf '.'
  sleep 5
 done
./ose_pvcreate_lun vg1 1G 400 
./ose_pvcreate_lun vg1 10G 20 
./ose_pvcreate_lun vg1 50G 4 
systemctl restart target.service
